sys_script_include.script:
var HealthScan = Class.create();
HealthScan.prototype = {
	initialize: function() {
	},

	validAccess : function(access , request){
		var result = false;

		try { 
			var r = new sn_ws.RESTMessageV2('x_alpa2_services.Health scan - Record Producer', 'Validar acesso');
			r.setStringParameterNoEscape('user', request.user);
			r.setStringParameterNoEscape('url', request.url);
			r.setBasicAuth(access.user, access.pwd);

			var response = r.execute();
			var responseBody = response.getBody();
			var httpStatus = response.getStatusCode();
			gs.info(httpStatus);
			if(httpStatus == 200){
				var responseJson = JSON.parse(responseBody);

				if(responseJson.result.length > 0){
					result = true;
					return result;
				}
			}
		}
		catch(ex) {
			var message = ex.message;
		}

		return result;
	},

	type: 'HealthScan'
};
