var PermissionHrUtils = Class.create();
PermissionHrUtils.prototype = {
	initialize: function() {
	},


	servicesRequest : function(solicitante, analista, envolvidos, grupo, usuario){
		var result = false;
		var isSolicitante = usuario == solicitante ? true : false;
		var isAnalista = usuario == analista ? true : false;
		var isEnvolvido = envolvidos.indexOf(usuario) != -1 ? true : false;
		var isGrupo = this._isMemberGroup(usuario, grupo);

		if(isSolicitante || isAnalista || isEnvolvido || isGrupo)
			result = true;

		return result;
	},

	_isMemberGroup : function(usuario, grupo){
		var result = false;


		return result;
	},

	type: 'PermissionHrUtils'
};
